                            {getStatusBadge(true, !!emailConfig.oauth2ClientId)}
                          </Label>
                          <TextInput
                            value={emailConfig.oauth2ClientId}
                            onChange={(e) => updateEmailConfig('oauth2ClientId', e.target.value)}
